    //     }, {
    //         name: "asdasdasdasdasdas",
    //         type: "log"
    //     }
    // ];
    const empty = [];

    const DashboardContext = useContext(Dashboard.Context);
    const headerContent = 
        <>
            <ButtonGroup buttons={[
                <Button icon={FaRegClock}>Recent Logs</Button>,
                <Button icon={CgMenuGridR}>All Logs</Button>,
                <Button icon={HiPlus}>New Log</Button>
            ]}/>
        </>
    ;    
    DashboardContext.setState((state) => {
        const newState = {...state};
        newState.headerContent = headerContent;
        return newState;
    });

    return(
        <Card>
            {/* <ButtonGroup buttons={[
                <Button icon={FaRegClock}>Recent Logs</Button>,
                <Button icon={CgMenuGridR}>All Logs</Button>,
                <Button icon={HiPlus}>New Log</Button>
            ]}/> */}
            <div className="h-4"/>
            <p className="text-lg font-bold">Your Most Recent Logs</p>
            <div className="h-4"/>
            <FileBrowser files={empty}/>
            <div className="h-4"/>
        </Card>
    )

}

export default ManageLogs;
